def lines = ["",
"",
"",
"",
""
]

def map = (0..8).inject([]) { list, num ->
    list.add( ["R": ((0..8).find { it - num ==  1 && num.intdiv(3) == it.intdiv(3) }),
               "L": ((0..8).find { it - num == -1 && num.intdiv(3) == it.intdiv(3) }),
               "U": ((0..8).find { it - num == -3 && num % 3 == it % 3 }),
               "D": ((0..8).find { it - num ==  3 && num % 3 == it % 3 }),
    ] )
    list
}

println map
def button = 4

lines.each { line ->
    line.split("").each { dir ->
        def newButton =  map[button][dir]
        if (newButton != null)
            button = newButton
    }
    println (button + 1)
}
